// Modules //
import React from 'react';
import axios from 'axios';
import Fade from 'react-reveal/Fade';

// Style //
import Button from 'react-bootstrap/Button';
import './Main.scss';

class Main extends React.Component {

  constructor() {
    super();

    this.state = {
      ArticlesPopulaire: [],
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8000/").then((data) => {
      //console.log(data.data);
      this.setState({
        ArticlesPopulaire: data.data,        
      })
      console.log(data);
    }).catch(function(error) {
      if (!error.status) {
        console.log(error);
      }
    })
  }

  render() {
    return (
      <div className="div_main">
        <Fade cascade>
          <div className="h1_nom-prenom">
            <h1 className="h1_nom">Fiona </h1>
            <h1 className="h1_prenom">Battaglia</h1>
          </div>
        </Fade>
        <div className="menu">
          <ul className="menu-ul">
            <li className="menu-li">menu 1</li>
            <li className="menu-li">menu 2</li>
            <li className="menu-li">menu 3</li>
          </ul>
        </div>
      </div>
    );
  }
}

export default Main;
