// Modules //
import React from 'react';
import axios from 'axios';
import Fade from 'react-reveal/Fade';

// Style //
import { Container, Col, Row, Card } from 'react-bootstrap';
import './Contact.scss';

class Contact extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  onNameChange(event) {
    this.setState({name: event.target.value})
  }

  onEmailChange(event) {
    this.setState({email: event.target.value})
  }

  onMessageChange(event) {
    this.setState({message: event.target.value})
  }

  handleSubmit(e){
    e.preventDefault();

    axios({
      method: "POST", 
      url:"http://localhost:3002/send", 
      data:  this.state
    }).then((response)=>{
      if (response.data.status === 'success'){
        alert("Message Sent."); 
        this.resetForm()
      }else if(response.data.status === 'fail'){
        alert("Message failed to send.")
      }
    })
  }

  render() {
    return (
      <div className="div_contact-coord">
        <Container>
            <Row>
            <Fade cascade>
            <div className="div_contact">
            <h2 className="h2_contact">Contacter Fiona</h2>
              <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                <div className="form-group">
                  <label className="label" htmlFor="name">Nom</label>
                  <input type="text" className="form-control" />
                </div>
                <div className="form-group">
                    <label className="label" htmlFor="exampleInputEmail1">Email</label>
                    <input type="email" className="form-control" aria-describedby="emailHelp" />
                </div>
                <div className="form-group">
                    <label className="label" htmlFor="message">Message</label>
                    <textarea className="form-control" rows="5"></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
                </form>
                </div>
                <div className="div_coord">
                <h2 className="h2_coord">Coordonnées</h2>
                <Card className="border-0 bg-transparent">
                    <Card.Body className="card_coord">
                        <Card.Title>Art et Dessin</Card.Title>
                        <Card.Subtitle>06 13 29 59 90</Card.Subtitle>
                        <Card.Title>Linguistique</Card.Title>
                        <Card.Subtitle>batta.fiona@gmail.com</Card.Subtitle>
                    </Card.Body>
                </Card>
                <Card className="border-0 bg-transparent">
                    <Card.Body className="card_cv">
                        <Card.Title>Télécharger CV</Card.Title>
                    </Card.Body>
                </Card>
                </div>
            </Fade>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Contact;
