// Modules //
import React from 'react';
import axios from 'axios';
import Fade from 'react-reveal/Fade';

// Style //
import { Container, Col, Row, Card } from 'react-bootstrap';
import './Contact.scss';

class Contact extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      name: '',
      email: '',
      message: ''
    }
  }

  onNameChange(event) {
    this.setState({name: event.target.value})
  }

  onEmailChange(event) {
    this.setState({email: event.target.value})
  }

  onMessageChange(event) {
    this.setState({message: event.target.value})
  }

  handleSubmit(event) {
  }

  render() {
    return (
      <div className="div_contact-coord">
        <Container>
            <Row>
            <Fade cascade>
            <div className="div_contact">
            <h2 className="h2_contact">Contactez Fiona</h2>
              <form id="contact-form" onSubmit={this.handleSubmit.bind(this)} method="POST">
                <div className="form-group">
                  <label htmlFor="name">Name</label>
                  <input type="text" className="form-control" />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Email address</label>
                    <input type="email" className="form-control" aria-describedby="emailHelp" />
                </div>
                <div className="form-group">
                    <label htmlFor="message">Message</label>
                    <textarea className="form-control" rows="5"></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
                </form>
                </div>
                <div className="div_coord">
                <h2 className="h2_coord">Coordonnees</h2>
                <Card className="border-0">
                    <Card.Body className="">
                        <Card.Title>Art et Dessin</Card.Title>
                        <Card.Subtitle>En tant que passe-temps</Card.Subtitle>
                        <Card.Title>Linguistique</Card.Title>
                        <Card.Subtitle>Cours suivis à l'université.</Card.Subtitle>
                    </Card.Body>
                </Card>
                </div>
            </Fade>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Contact;
